INSERT INTO `SERVMASK_PREFIX_posts` VALUES ('2','1','2015-11-01 20:16:22','2015-11-01 20:16:22','<h3 style=\"text-align: center;\">Vítám Vás na stránkách</h3>\r\n&nbsp;\r\n<p style=\"text-align: center;\">Vaše rozhodnutí podívat se na mé stránky, může být zásadním zlomem ve vašem životě.</p>\r\n<p style=\"text-align: center;\">Nabízím služby, které Vám mohou pomoci k radikální změně zdravotního stavu, hlubšímu pohledu na život, zlepšení nebo obnově vztahů s vašimi nejbližšími i duchovnějšímu prožívání vaší životní cesty.</p>\r\n<p style=\"text-align: center;\">Radím, pomáhám, učím, upozorňuji, pěstuji naději a to vše s otevřeným srdcem a mnoholetou praxí.</p>\r\n<p style=\"text-align: center;\"><strong>Přeji Vám, ať jsou mé stránky pro Vás inspirací, povzbuzením a nadějí…</strong></p>\r\n<p style=\"text-align: right;\"><em>Sam Fujera…</em></p>','Úvod','','publish','closed','open','','uvod','','','2015-11-01 22:24:19','2015-11-01 21:24:19','','0','http://samfujera.dev/?page_id=2','0','page','','0');
